import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth } from "./auth";
import multer from "multer";
import path from "path";
import { 
  insertIssueSchema, 
  insertCategorySchema, 
  insertAssignmentSchema, 
  resolveIssueSchema 
} from "@shared/schema";

// Configure multer for file uploads
const upload = multer({
  dest: 'uploads/',
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = /jpeg|jpg|png|gif|mp4|mov|avi/;
    const extname = allowedTypes.test(path.extname(file.originalname).toLowerCase());
    const mimetype = allowedTypes.test(file.mimetype);

    if (mimetype && extname) {
      return cb(null, true);
    } else {
      cb(new Error('Only images and videos are allowed'));
    }
  }
});

function requireAuth(req: any, res: any, next: any) {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "Authentication required" });
  }
  next();
}

function requireRole(roles: string[]) {
  return (req: any, res: any, next: any) => {
    if (!req.user || !roles.includes(req.user.role)) {
      return res.status(403).json({ message: "Insufficient permissions" });
    }
    next();
  };
}

export async function registerRoutes(app: Express): Promise<Server> {
  setupAuth(app);

  // States and cities data - using static data for demo
  app.get("/api/states", (req, res) => {
    res.json([
      { value: "california", label: "California" },
      { value: "new-york", label: "New York" },
      { value: "texas", label: "Texas" },
      { value: "florida", label: "Florida" },
    ]);
  });

  app.get("/api/cities/:state", (req, res) => {
    const cities = {
      california: [
        { value: "san-francisco", label: "San Francisco" },
        { value: "los-angeles", label: "Los Angeles" },
        { value: "san-diego", label: "San Diego" },
      ],
      "new-york": [
        { value: "new-york-city", label: "New York City" },
        { value: "buffalo", label: "Buffalo" },
        { value: "rochester", label: "Rochester" },
      ],
      texas: [
        { value: "houston", label: "Houston" },
        { value: "dallas", label: "Dallas" },
        { value: "austin", label: "Austin" },
      ],
      florida: [
        { value: "miami", label: "Miami" },
        { value: "orlando", label: "Orlando" },
        { value: "tampa", label: "Tampa" },
      ],
    };

    res.json(cities[req.params.state as keyof typeof cities] || []);
  });

  // Categories
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  app.post("/api/categories", requireAuth, requireRole(["superadmin"]), async (req, res) => {
    try {
      const categoryData = insertCategorySchema.parse(req.body);
      const category = await storage.createCategory(categoryData);
      res.status(201).json(category);
    } catch (error) {
      res.status(400).json({ message: "Invalid category data" });
    }
  });

  // Issues
  app.get("/api/issues", async (req, res) => {
    try {
      const { state, city, status, userId } = req.query;
      const filters: any = {};

      if (state) filters.state = state as string;
      if (city) filters.city = city as string;
      if (status) filters.status = status as string;
      if (userId) filters.userId = userId as string;

      const issues = await storage.getIssues(filters);
      res.json(issues);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch issues" });
    }
  });

  app.get("/api/issues/:id", async (req, res) => {
    try {
      const userId = req.user?.id;
      const issue = await storage.getIssue(req.params.id, userId);
      if (!issue) {
        return res.status(404).json({ message: "Issue not found" });
      }
      res.json(issue);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch issue" });
    }
  });

  app.post("/api/issues", requireAuth, upload.array('media', 5), async (req, res) => {
    try {
      const files = req.files as Express.Multer.File[];
      const mediaUrls = files?.map(file => `/uploads/${file.filename}`) || [];

      const issueData = insertIssueSchema.parse({
        ...req.body,
        userId: req.user!.id,
        mediaUrls,
      });

      const issue = await storage.createIssue(issueData);
      res.status(201).json(issue);
    } catch (error) {
      res.status(400).json({ message: "Invalid issue data" });
    }
  });

  app.post("/api/issues/:id/upvote", requireAuth, async (req, res) => {
    try {
      const result = await storage.toggleUpvote(req.user!.id, req.params.id);
      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to toggle upvote" });
    }
  });

  app.post("/api/issues/:id/resolve", requireAuth, requireRole(["admin"]), upload.single('resolutionImage'), async (req, res) => {
    try {
      const resolutionImageUrl = req.file ? `/uploads/${req.file.filename}` : '';
      const resolveData = resolveIssueSchema.parse({
        resolutionNotes: req.body.resolutionNotes,
        resolutionImageUrl,
      });

      await storage.resolveIssue(req.params.id, resolveData, req.user!.id);
      res.json({ message: "Issue resolved successfully" });
    } catch (error) {
      res.status(400).json({ message: "Failed to resolve issue" });
    }
  });

  // Assignments
  app.post("/api/issues/:id/assign", requireAuth, requireRole(["superadmin"]), async (req, res) => {
    try {
      const { adminIds, assignmentNotes } = req.body;

      if (!adminIds || !Array.isArray(adminIds) || adminIds.length === 0) {
        return res.status(400).json({ message: "At least one admin must be selected" });
      }

      const assignments = [];
      for (const adminId of adminIds) {
        const assignmentData = insertAssignmentSchema.parse({
          issueId: req.params.id,
          adminId,
          assignedBy: req.user!.id,
          assignmentNotes,
        });

        const assignment = await storage.assignIssue(assignmentData);
        assignments.push(assignment);
      }

      res.json(assignments);
    } catch (error) {
      res.status(400).json({ message: "Failed to assign issue" });
    }
  });

  app.get("/api/admin/issues", requireAuth, requireRole(["admin"]), async (req, res) => {
    try {
      const issues = await storage.getAssignedIssues(req.user!.id);
      res.json(issues);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch assigned issues" });
    }
  });

  // Admin management
  app.get("/api/admins", requireAuth, requireRole(["superadmin"]), async (req, res) => {
    try {
      const admins = await storage.getAdminUsers();
      res.json(admins);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch admins" });
    }
  });

  // Analytics
  app.get("/api/analytics", requireAuth, requireRole(["admin", "superadmin"]), async (req, res) => {
    try {
      const analytics = await storage.getAnalytics();
      res.json(analytics);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  // Serve uploaded files
  app.use('/uploads', (req, res, next) => {
    // Simple static file serving for uploads
    // In production, you'd want to use a proper file server or CDN
    res.sendFile(path.join(process.cwd(), req.path));
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  issues, 
  categories, 
  upvotes, 
  assignments,
  type User, 
  type InsertUser,
  type Issue,
  type InsertIssue,
  type Category,
  type InsertCategory,
  type Upvote,
  type InsertUpvote,
  type Assignment,
  type InsertAssignment,
  type ResolveIssue
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, sql, inArray } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { pool } from "./db";

const PostgresSessionStore = connectPg(session);

export interface IStorage {
  sessionStore: any;

  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  getAdminUsers(): Promise<User[]>;

  // Category operations
  getCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Issue operations
  getIssues(filters?: {
    state?: string;
    city?: string;
    status?: string;
    userId?: string;
    assignedToAdmin?: string;
  }): Promise<(Issue & { 
    category: Category | null; 
    user: User | null; 
    upvoteCount: number;
    userUpvoted?: boolean;
    resolver?: User | null;
  })[]>;
  getIssue(id: string, userId?: string): Promise<(Issue & { 
    category: Category | null; 
    user: User | null; 
    upvoteCount: number;
    userUpvoted?: boolean;
    resolver?: User | null;
  }) | undefined>;
  createIssue(issue: InsertIssue): Promise<Issue>;
  updateIssueStatus(id: string, status: string): Promise<void>;
  resolveIssue(id: string, data: ResolveIssue, resolvedBy: string): Promise<void>;

  // Upvote operations
  toggleUpvote(userId: string, issueId: string): Promise<{ upvoted: boolean; count: number }>;

  // Assignment operations
  assignIssue(assignment: InsertAssignment): Promise<Assignment>;
  getAssignedIssues(adminId: string): Promise<(Issue & { 
    category: Category | null; 
    user: User | null; 
    upvoteCount: number;
  })[]>;

  // Analytics
  getAnalytics(): Promise<{
    totalIssues: number;
    notAssigned: number;
    assigned: number;
    resolved: number;
    categoryBreakdown: { name: string; count: number }[];
    mostUpvotedIssues: (Issue & { 
      category: Category | null; 
      upvoteCount: number;
    })[];
  }>;
}

export class DatabaseStorage implements IStorage {
  sessionStore: any;

  constructor() {
    this.sessionStore = new PostgresSessionStore({ 
      pool, 
      createTableIfMissing: true 
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getAdminUsers(): Promise<User[]> {
    return await db.select().from(users).where(eq(users.role, "admin"));
  }

  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories);
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db
      .insert(categories)
      .values(category)
      .returning();
    return newCategory;
  }

  async getIssues(filters?: {
    state?: string;
    city?: string;
    status?: string;
    userId?: string;
    assignedToAdmin?: string;
  }): Promise<(Issue & { 
    category: Category | null; 
    user: User | null; 
    upvoteCount: number;
    userUpvoted?: boolean;
    resolver?: User | null;
  })[]> {
    let query = db
      .select({
        issue: issues,
        category: categories,
        user: users,
        upvoteCount: sql<number>`COALESCE(${sql`COUNT(${upvotes.id})`}, 0)`,
        resolver: sql<User | null>`NULL`,
      })
      .from(issues)
      .leftJoin(categories, eq(issues.categoryId, categories.id))
      .leftJoin(users, eq(issues.userId, users.id))
      .leftJoin(upvotes, eq(issues.id, upvotes.issueId))
      .groupBy(issues.id, categories.id, users.id);

    if (filters?.assignedToAdmin) {
      query = query
        .innerJoin(assignments, eq(issues.id, assignments.issueId));
    }

    const conditions = [];
    if (filters?.assignedToAdmin) conditions.push(eq(assignments.adminId, filters.assignedToAdmin));
    if (filters?.state) conditions.push(eq(issues.state, filters.state));
    if (filters?.city) conditions.push(eq(issues.city, filters.city));
    if (filters?.status) conditions.push(eq(issues.status, filters.status));
    if (filters?.userId) conditions.push(eq(issues.userId, filters.userId));

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    query = query.orderBy(desc(issues.createdAt));

    const results = await query;

    return results.map(result => ({
      ...result.issue,
      category: result.category,
      user: result.user,
      upvoteCount: result.upvoteCount,
      resolver: result.resolver,
    }));
  }

  async getIssue(id: string, userId?: string): Promise<(Issue & { 
    category: Category | null; 
    user: User | null; 
    upvoteCount: number;
    userUpvoted?: boolean;
    resolver?: User | null;
  }) | undefined> {
    const result = await db
      .select({
        issue: issues,
        category: categories,
        user: users,
        resolver: sql<User | null>`resolver_user`,
        upvoteCount: sql<number>`COALESCE(upvote_count.count, 0)`,
        userUpvoted: userId ? sql<boolean>`CASE WHEN user_upvote.id IS NOT NULL THEN true ELSE false END` : sql<boolean>`false`,
      })
      .from(issues)
      .leftJoin(categories, eq(issues.categoryId, categories.id))
      .leftJoin(users, eq(issues.userId, users.id))
      .leftJoin(
        sql`(SELECT issue_id, COUNT(*) as count FROM upvotes GROUP BY issue_id) upvote_count`,
        sql`upvote_count.issue_id = ${issues.id}`
      )
      .leftJoin(
        sql`users resolver_user`,
        eq(issues.resolvedBy, sql`resolver_user.id`)
      )
      .leftJoin(
        userId ? sql`upvotes user_upvote` : sql`(SELECT NULL as id, NULL as user_id, NULL as issue_id) user_upvote`,
        userId ? and(eq(sql`user_upvote.issue_id`, issues.id), eq(sql`user_upvote.user_id`, userId)) : sql`false`
      )
      .where(eq(issues.id, id))
      .limit(1);

    const [row] = result;
    if (!row) return undefined;

    return {
      ...row.issue,
      category: row.category,
      user: row.user,
      upvoteCount: row.upvoteCount,
      userUpvoted: row.userUpvoted,
      resolver: row.resolver,
    };
  }

  async createIssue(issue: InsertIssue): Promise<Issue> {
    const [newIssue] = await db
      .insert(issues)
      .values(issue)
      .returning();
    return newIssue;
  }

  async updateIssueStatus(id: string, status: string): Promise<void> {
    await db
      .update(issues)
      .set({ status })
      .where(eq(issues.id, id));
  }

  async resolveIssue(id: string, data: ResolveIssue, resolvedBy: string): Promise<void> {
    await db
      .update(issues)
      .set({
        status: "resolved",
        resolutionNotes: data.resolutionNotes,
        resolutionImageUrl: data.resolutionImageUrl,
        resolvedBy,
        resolvedAt: new Date(),
      })
      .where(eq(issues.id, id));
  }

  async toggleUpvote(userId: string, issueId: string): Promise<{ upvoted: boolean; count: number }> {
    const existingUpvote = await db
      .select()
      .from(upvotes)
      .where(and(eq(upvotes.userId, userId), eq(upvotes.issueId, issueId)))
      .limit(1);

    if (existingUpvote.length > 0) {
      // Remove upvote
      await db
        .delete(upvotes)
        .where(and(eq(upvotes.userId, userId), eq(upvotes.issueId, issueId)));
    } else {
      // Add upvote
      await db
        .insert(upvotes)
        .values({ userId, issueId });
    }

    const [countResult] = await db
      .select({ count: sql<number>`COUNT(*)` })
      .from(upvotes)
      .where(eq(upvotes.issueId, issueId));

    return {
      upvoted: existingUpvote.length === 0,
      count: countResult.count,
    };
  }

  async assignIssue(assignment: InsertAssignment): Promise<Assignment> {
    const [newAssignment] = await db
      .insert(assignments)
      .values(assignment)
      .returning();

    await this.updateIssueStatus(assignment.issueId!, "assigned");

    return newAssignment;
  }

  async getAssignedIssues(adminId: string): Promise<(Issue & { 
    category: Category | null; 
    user: User | null; 
    upvoteCount: number;
  })[]> {
    return this.getIssues({ assignedToAdmin: adminId, status: "assigned" });
  }

  async getAnalytics(): Promise<{
    totalIssues: number;
    notAssigned: number;
    assigned: number;
    resolved: number;
    categoryBreakdown: { name: string; count: number }[];
    mostUpvotedIssues: (Issue & { 
      category: Category | null; 
      upvoteCount: number;
    })[];
  }> {
    const [totalResult] = await db
      .select({ count: sql<number>`COUNT(*)` })
      .from(issues);

    const [notAssignedResult] = await db
      .select({ count: sql<number>`COUNT(*)` })
      .from(issues)
      .where(eq(issues.status, "not-assigned"));

    const [assignedResult] = await db
      .select({ count: sql<number>`COUNT(*)` })
      .from(issues)
      .where(eq(issues.status, "assigned"));

    const [resolvedResult] = await db
      .select({ count: sql<number>`COUNT(*)` })
      .from(issues)
      .where(eq(issues.status, "resolved"));

    const categoryBreakdown = await db
      .select({
        name: categories.name,
        count: sql<number>`COUNT(${issues.id})`,
      })
      .from(categories)
      .leftJoin(issues, eq(categories.id, issues.categoryId))
      .groupBy(categories.id, categories.name);

    const mostUpvotedIssues = await db
      .select({
        issue: issues,
        category: categories,
        upvoteCount: sql<number>`COALESCE(COUNT(${upvotes.id}), 0)`,
      })
      .from(issues)
      .leftJoin(categories, eq(issues.categoryId, categories.id))
      .leftJoin(upvotes, eq(issues.id, upvotes.issueId))
      .groupBy(issues.id, categories.id)
      .orderBy(desc(sql`COUNT(${upvotes.id})`))
      .limit(3);

    return {
      totalIssues: totalResult.count,
      notAssigned: notAssignedResult.count,
      assigned: assignedResult.count,
      resolved: resolvedResult.count,
      categoryBreakdown,
      mostUpvotedIssues: mostUpvotedIssues.map(item => ({
        ...item.issue,
        category: item.category,
        upvoteCount: item.upvoteCount,
      })),
    };
  }
}

export const storage = new DatabaseStorage();

